package functions;

public class loopingCode {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int A = 10; A<20; A++) {System.out.println(A);}
		
		
		int B = 100;
			
		int sum = 0;
		
		for(int C = 1; C<=B; ++C) {
			
		sum+=C;
				
		}
		
		System.out.println("sum=" + sum);
		
		
	}

}
